// Satu node saja untuk semua processing
const products = $input.all()

// Generate tanggal
const now = new Date();
const formattedDate = now.toLocaleString('id-ID', {
  timeZone: 'Asia/Jakarta',
  day: '2-digit',
  month: '2-digit',
  year: 'numeric',
  hour: '2-digit',
  minute: '2-digit'
});

// Pastikan ada produk
if (products.length === 0) {
  const fallbackMessage = `📦 TIDAK ADA PRODUK\nTanggal: ${formattedDate}`;
  return [{ json: { message: fallbackMessage } }];
}

// Build message
let message = `🛍️ *DAFTAR PRODUK* \n📅 ${formattedDate}\n────────────────\n`;

products.forEach((product, index) => {
  const item = product.json;
  const displayName = item.name ? item.name.toUpperCase() : 'PRODUK';
  message += `*${index}.* ${displayName} - Stok: ${item.stock || 0}\n`;
});

message += `────────────────\n🛒 Ketik angka (0-${products.length-1}) untuk beli:`;

return [{ json: { message, products: products.map(p => p.json) } }];

// Ambil chatId dari Telegram Trigger
const chatId = $('Telegram Trigger1').first().json.message.chat.id;

// Ambil hasil MySQL (semua row)
const rows = $input.all().map(item => item.json);

let message = "╭────────────✧\n";
message += "┊   LIST PRODUK\n";
message += "┊   Page 1/1\n";
message += "┊─────────────\n";

// Loop hasil query
rows.forEach((row, index) => {
  message += `┊ [${index + 1}] ${row.name.toUpperCase()} (Stock: ${row.stock})\n`;
});

message += "╰────────────✧";

// Return ke node berikutnya
return [{
  json: { chatId, message }
}];

